package t.mk.three.jiitassignmentimport android.app.Applicationimport androidx.appcompat.app.AppCompatDelegateimport coil.ImageLoaderimport coil.ImageLoaderFactoryimport coil.memory.MemoryCacheimport org.koin.android.ext.koin.androidContextimport org.koin.android.ext.koin.androidLoggerimport org.koin.core.context.GlobalContextimport org.koin.core.logger.Levelimport t.mk.three.jiitassignment.data.di.coreModuleimport t.mk.three.jiitassignment.feature.detail.di.stockDetailModuleimport t.mk.three.jiitassignment.feature.main.di.mainModuleclass RankingApp: Application(), ImageLoaderFactory {    override fun onCreate() {        super.onCreate()        GlobalContext.startKoin {            androidLogger(if (BuildConfig.DEBUG) Level.ERROR else Level.NONE)            androidContext(applicationContext)            modules(coreModule, mainModule, stockDetailModule)        }        AppCompatDelegate.setDefaultNightMode(AppCompatDelegate.MODE_NIGHT_NO)    }    override fun newImageLoader(): ImageLoader {        return ImageLoader.Builder(this)            .memoryCache {                MemoryCache.Builder(this)                    .maxSizePercent(0.25)                    .build()            }            .crossfade(true)            .build()    }}