package t.mk.three.jiitassignment.feature.detail.repositoryimport com.apollographql.apollo3.api.Optionalimport t.mk.three.jiitassignment.StockDetailQueryimport t.mk.three.jiitassignment.data.RankingApolloClientclass StockDetailRepository(    private val apolloClient: RankingApolloClient): BaseStockDetailRepository {    override suspend fun getStockDetail(id: String): RankingApolloClient.Result<StockDetailQuery.Data> {        val query = StockDetailQuery(            id = Optional.present(id)        )        return apolloClient.query(query)    }}