package t.mk.three.jiitassignment.feature.detail.viewimport androidx.core.view.isVisibleimport androidx.recyclerview.widget.RecyclerViewimport t.mk.three.jiitassignment.databinding.ViewCompanyInfoBindingclass CompanyInfoViewHolder(    private val binding: ViewCompanyInfoBinding): RecyclerView.ViewHolder(binding.root) {    fun init(        sector: String?,        industry: String?,        website: String?    ) {        sector?.let {            binding.vSectorContainer.isVisible = true            binding.tvSector.text = it        } ?: run {            binding.vSectorContainer.isVisible = false        }        industry?.let {            binding.vIndustryContainer.isVisible = true            binding.tvIndustry.text = it        } ?: run {            binding.vIndustryContainer.isVisible = false        }        website?.let {            binding.vWebsiteContainer.isVisible = true            binding.tvWebsite.text = it        } ?: run {            binding.vWebsiteContainer.isVisible = false        }    }}