package t.mk.three.jiitassignment.feature.detail.viewimport android.graphics.Typefaceimport android.text.SpannableStringBuilderimport android.text.Spannedimport android.text.style.StyleSpanimport androidx.recyclerview.widget.RecyclerViewimport t.mk.three.jiitassignment.Rimport t.mk.three.jiitassignment.databinding.ViewCompanyDescriptionBindingprivate const val MAX_PREVIEW_TEXT_LENGTH = 200class DescriptionViewHolder(    private val binding: ViewCompanyDescriptionBinding): RecyclerView.ViewHolder(binding.root) {    private var isExpand: Boolean = false    fun init(description: String) {        binding.root.setOnClickListener {            isExpand = !isExpand            updateText(description)        }        updateText(description)    }    private fun updateText(description: String) {        val isExpandable = description.length > MAX_PREVIEW_TEXT_LENGTH        binding.tvDescription.text = if (isExpandable && !isExpand) {            val spanText = SpannableStringBuilder()            val readMore = binding.root.resources.getString(R.string.read_more_text)            spanText.append(                description.take(MAX_PREVIEW_TEXT_LENGTH)            )            spanText.append(                readMore            )            spanText.setSpan(                StyleSpan(Typeface.BOLD),                MAX_PREVIEW_TEXT_LENGTH,                MAX_PREVIEW_TEXT_LENGTH + readMore.length,                Spanned.SPAN_EXCLUSIVE_INCLUSIVE            )            spanText        } else {            description        }    }}