package t.mk.three.jiitassignment.feature.detail.viewimport android.content.res.ColorStateListimport androidx.annotation.ColorResimport androidx.core.content.ContextCompatimport androidx.recyclerview.widget.RecyclerViewimport t.mk.three.jiitassignment.Rimport t.mk.three.jiitassignment.databinding.ViewFactorScoreBindingclass FactorViewHolder(    private val binding: ViewFactorScoreBinding) : RecyclerView.ViewHolder(binding.root) {    fun init(max: Int, progress: Int, title: String, @ColorRes color: Int = R.color.primaryColor) {        binding.pbScore.progressTintList = ColorStateList.valueOf(ContextCompat.getColor(binding.root.context, color))        binding.pbScore.progress = progress        binding.pbScore.max = max        binding.tvScoreTitle.text = title        binding.tvScore.text = progress.toString()    }}