package t.mk.three.jiitassignment.feature.detail.viewimport android.annotation.SuppressLintimport android.content.Contextimport android.util.AttributeSetimport android.view.MotionEventimport com.github.mikephil.charting.charts.LineChartclass JittaLineChart @JvmOverloads constructor(context: Context, attrs: AttributeSet? = null, defStyleAttr: Int = 0) : LineChart(context, attrs, defStyleAttr) {    @SuppressLint("ClickableViewAccessibility")    override fun onTouchEvent(event: MotionEvent?): Boolean {        when (event?.action) {            MotionEvent.ACTION_DOWN,            MotionEvent.ACTION_MOVE -> {                parent?.requestDisallowInterceptTouchEvent(true)            }            MotionEvent.ACTION_CANCEL,            MotionEvent.ACTION_UP -> {                parent?.requestDisallowInterceptTouchEvent(false)            }        }        return super.onTouchEvent(event)    }}