package t.mk.three.jiitassignment.feature.detail.viewimport android.content.Contextimport android.util.AttributeSetimport android.view.MotionEventimport com.github.mikephil.charting.animation.Easingimport com.github.mikephil.charting.charts.LineChartimport com.github.mikephil.charting.components.XAxisclass JittaLineChart @JvmOverloads constructor(context: Context, attrs: AttributeSet? = null, defStyleAttr: Int = 0) : LineChart(context, attrs, defStyleAttr) {    init {        axisRight.isEnabled = false        description.isEnabled = false        animateXY(1000, 1000, Easing.EaseInExpo)        xAxis.position = XAxis.XAxisPosition.BOTTOM        setTouchEnabled(true)        isDragEnabled = true        setScaleEnabled(false)        setPinchZoom(false)        isDoubleTapToZoomEnabled = false        xAxis.labelRotationAngle = 270f    }    override fun onTouchEvent(event: MotionEvent?): Boolean {        when (event?.action) {            MotionEvent.ACTION_DOWN,            MotionEvent.ACTION_MOVE -> {                parent?.requestDisallowInterceptTouchEvent(true)            }            MotionEvent.ACTION_CANCEL,            MotionEvent.ACTION_UP -> {                parent?.requestDisallowInterceptTouchEvent(false)            }        }        return super.onTouchEvent(event)    }}