package t.mk.three.jiitassignment.feature.detail.viewimport androidx.recyclerview.widget.RecyclerViewimport t.mk.three.jiitassignment.Rimport t.mk.three.jiitassignment.databinding.ViewSignIpoContainerBindingclass SignIPOViewHolder(    private val binding: ViewSignIpoContainerBinding): RecyclerView.ViewHolder(binding.root) {    fun init(value: Int?, currency: String?, title: String?) {        title?.let {            binding.tvTitle.text = title        }        binding.tvPrice.text = binding.root.context.resources.getString(R.string.ipo_price_text, "($currency)".takeIf { !currency.isNullOrBlank() } ?: "", value.toString())    }}