package t.mk.three.jiitassignment.feature.detail.viewimport android.view.ViewGroupimport androidx.core.content.ContextCompatimport androidx.core.view.isVisibleimport androidx.recyclerview.widget.RecyclerViewimport t.mk.three.jiitassignment.Rimport t.mk.three.jiitassignment.databinding.ViewSignTableContainerBindingclass SignTableViewHolder(    private val binding: ViewSignTableContainerBinding) : RecyclerView.ViewHolder(binding.root) {    fun init(id: String, title: String, detail: String, isExpand: Boolean, recyclerView: RecyclerView, color: Int = R.color.primaryColor, onSignClicked: (String) -> Unit) {        binding.root.setOnClickListener {            onSignClicked(id)        }        binding.cvVerticalBar.setCardBackgroundColor(ContextCompat.getColor(binding.root.context, color))        binding.tvTitle.text = title        binding.tvDetail.text = detail        binding.vArrow.rotation = if (isExpand) 180f else 0f        binding.vTableContainer.isVisible = isExpand        (recyclerView.parent as? ViewGroup)?.removeView(recyclerView)        binding.vTableContainer.addView(recyclerView)    }}