package t.mk.three.jiitassignment.feature.detail.viewimport androidx.recyclerview.widget.RecyclerViewimport t.mk.three.jiitassignment.Rimport t.mk.three.jiitassignment.databinding.ViewStockDetailHeaderBindingimport t.mk.three.jiitassignment.extension.thousandsimport t.mk.three.jiitassignment.feature.detail.adapter.StockDetailAdapterclass StockDetailHeaderViewHolder(    private val binding: ViewStockDetailHeaderBinding): RecyclerView.ViewHolder(binding.root) {    fun init(stock: StockDetailAdapter.Item.Header) {        val context = binding.root.context        binding.tvName.text = stock.name        binding.tvSymbol.text = context.resources.getString(R.string.stock_symbol, stock.market, stock.symbol)        binding.tvScore.text = stock.score?.thousands(2) ?: "N/A"        binding.tvRanking.text = context.resources.getString(R.string.jitta_ranking_title, stock.rank.toString(), stock.member.toString())    }}