package t.mk.three.jiitassignment.feature.detail.viewimport android.content.Contextimport android.widget.LinearLayoutimport android.widget.TextViewimport androidx.core.content.ContextCompatimport androidx.core.view.setPaddingimport androidx.recyclerview.widget.RecyclerViewimport t.mk.three.jiitassignment.Rimport t.mk.three.jiitassignment.databinding.ViewTableDetailBindingimport t.mk.three.jiitassignment.databinding.ViewTableHeaderBindingclass TableDetailViewHolder(    private val binding: ViewTableDetailBinding) : RecyclerView.ViewHolder(binding.root) {    fun init(header: String, values: List<String?>) {        binding.tvHeader.text = header        values.forEach { value ->            val layoutParam = LinearLayout.LayoutParams(LinearLayout.LayoutParams.WRAP_CONTENT, LinearLayout.LayoutParams.WRAP_CONTENT)            binding.vContentContainer.addView(getHeaderView(binding.root.context, value), layoutParam)        }    }    private fun getHeaderView(context: Context, text: String?): TextView {        return TextView(binding.root.context).apply {            setPadding(context.resources.getDimensionPixelSize(R.dimen.spacing_12))            textSize = 13f            setTextColor(ContextCompat.getColor(context, R.color.fontPrimary))            this.text = text        }    }}