package t.mk.three.jiitassignment.feature.detail.viewimport android.content.Contextimport android.widget.TextViewimport androidx.core.content.ContextCompatimport androidx.core.view.setPaddingimport androidx.recyclerview.widget.RecyclerViewimport t.mk.three.jiitassignment.Rimport t.mk.three.jiitassignment.databinding.ViewTableHeaderBindingclass TableHeaderViewHolder(    private val binding: ViewTableHeaderBinding) : RecyclerView.ViewHolder(binding.root) {    fun init(headers: List<String?>) {        headers.forEach { header ->            binding.vContentContainer.addView(getHeaderView(binding.root.context, header))        }    }    private fun getHeaderView(context: Context, text: String?): TextView {        return TextView(binding.root.context).apply {            setPadding(context.resources.getDimensionPixelSize(R.dimen.spacing_12))            textSize = 13f            setTextColor(ContextCompat.getColor(context, R.color.fontPrimary))            this.text = text        }    }}