package t.mk.three.jiitassignment.feature.main.viewimport androidx.recyclerview.widget.RecyclerViewimport t.mk.three.jiitassignment.Rimport t.mk.three.jiitassignment.databinding.ViewStockBindingimport t.mk.three.jiitassignment.extension.thousandsimport t.mk.three.jiitassignment.feature.main.viewModel.MainViewModelclass StockViewHolder(    private val binding: ViewStockBinding): RecyclerView.ViewHolder(binding.root) {    fun init(stock: MainViewModel.Stock, onStockClicked: (id: String, name: String?) -> Unit) {        val context = binding.root.context        binding.cvContainer.setOnClickListener {            stock.id?.let {                onStockClicked(stock.id, stock.title)            }        }        binding.tvName.text = stock.title        binding.tvSymbol.text = context.resources.getString(R.string.stock_symbol, stock.exchange, stock.symbol)        binding.tvRank.text = stock.rank.toString()        binding.tvStockCount.text = context.resources.getString(R.string.stock_count, stock.count.toString())        binding.tvPrice.text = stock.latestPrice?.thousands(4)    }}